    public static Integer MaKH;
    HocVienDAO dao = new HocVienDAO();
    NguoiHocDAO nhdao = new NguoiHocDAO();

//    void init() {
//        setIconImage(ShareHelper.APP_ICON);
//        setLocationRelativeTo(null);
//    }

    void fillComboBox() {
        DefaultComboBoxModel model = (DefaultComboBoxModel) cboNguoiHoc.getModel();
        model.removeAllElements();
        try {
            List<NguoiHoc> list = nhdao.selectByCourse(MaKH);
            for (NguoiHoc nh : list) {
                model.addElement(nh);
            }
        } catch (Exception e) {
            DialogHelper.alert(this, "Lỗi truy vấn học viên!");
        }
    }
    void fillGridView() {
        DefaultTableModel model = (DefaultTableModel) tblGridView.getModel();
        model.setRowCount(0);
        try {
            String sql = "SELECT hv.*, nh.HoTen FROM HocVien hv "
                    + " JOIN NguoiHoc nh ON nh.MaNH=hv.MaNH WHERE MaKH=?";
            ResultSet rs = JdbcHelper.executeQuery(sql, MaKH);
            while (rs.next()) {
                double diem = rs.getDouble("Diem");
                Object[] row = {
                    rs.getInt("MaHV"), rs.getString("MaNH"), rs.getString("HoTen"), diem, false
                };
                if (rdoTatCa.isSelected()) {
                    model.addRow(row);
                } else if (rdoDaNhap.isSelected() && diem >= 0) {
                    model.addRow(row);
                } else if (rdoChuaNhap.isSelected() && diem < 0) {
                    model.addRow(row);
                }
            }
        } catch (SQLException e) {
            DialogHelper.alert(this, "Lỗi truy vấn học viên!");
        }
    }

    void insert() {
        NguoiHoc nguoiHoc = (NguoiHoc) cboNguoiHoc.getSelectedItem();
        com.polypro.model.HocVien model = new com.polypro.model.HocVien();
        model.setMaKH(MaKH);
        model.setMaNH(nguoiHoc.getMaNH());
        try {
            if(Double.valueOf(txtDiem.getText()) < 0 || Double.valueOf(txtDiem.getText()) > 10) {
                JOptionPane.showMessageDialog(this, "Điểm không hợp lệ");
                return;
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Điểm không hợp lệ");
            return;
        }
        if(txtDiem.getText().length()==0){
            txtDiem.setText("-1");
        }
        model.setDiem(Double.valueOf(txtDiem.getText()));
        try {
            dao.insert(model);
            this.fillComboBox();
            this.fillGridView();
        } catch (Exception e) {
            System.out.println(e);
            DialogHelper.alert(this, "Lỗi thêm học viên vào khóa học!");
        }
    }

    void update() {
        for (int i = 0; i < tblGridView.getRowCount(); i++) {
            Integer mahv = (Integer) tblGridView.getValueAt(i, 0);
            String manh = (String) tblGridView.getValueAt(i, 1);
            double diem;
            try {
                diem = Double.parseDouble(String.valueOf(tblGridView.getValueAt(i, 3)));
                if(diem < -1 || diem > 10){
                    JOptionPane.showMessageDialog(this, "Điểm không hợp lệ");
                    return;
                }
            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, "Điểm không hợp lệ");
                return;
            }
                com.polypro.model.HocVien model = new com.polypro.model.HocVien();
                model.setMaHV(mahv);
                model.setMaKH(MaKH);
                model.setMaNH(manh);
                model.setDiem(diem);
                dao.update(model);
        }
        this.fillComboBox();
        this.fillGridView();
        DialogHelper.alert(this, "Cập nhật thành công!");
    }
    void delete() {
        int index = tblGridView.getSelectedRow();
        Integer mahv = (Integer) tblGridView.getValueAt(index, 0);
        dao.delete(mahv);
        this.fillComboBox();
        this.fillGridView();
    }
